 ExUnit.start()
 
 defmodule DayOneTest do
   use ExUnit.Case, async: true
   import AdventOne

   test "should decrement floors" do
     assert findFloor(")))") == -3;
   end

   test "should increment floors" do
     assert findFloor("(((") == 3;
   end
   
   test "should calculate floor correctly" do
     assert findFloor("))(()))") == -3;
   end

   test "should return 0 with empty instructions" do
